def raw_to_escaped(raw_str):
    def replace_match(match):
        escape_seq = match.group(1)
        # å¸¸è§è½¬ä¹‰å­—ç¬¦æ˜ å°„
        escape_map = {
            r'\n': '\n',
            r'\t': '\t',
            r'\r': '\r',
            r'\\': '\\',
            r'\"': '"',
            r'\'': "'",
        }
        return escape_map.get(escape_seq, escape_seq)  # å¦‚æœä¸æ˜¯ç‰¹æ®Šå­—ç¬¦ï¼ŒåŸæ ·è¿”å›

    # æ­£åˆ™åŒ¹é… \ + ç‰¹å®šå­—ç¬¦ï¼ˆå¦‚ \n, \t ç­‰ï¼‰
    return re.sub(r'\\([nrt\\"\'])', replace_match, raw_str)


