

import re


def raw_to_escaped(raw_str):
    def replace_match(match):
        escape_seq = match.group(1)
        # 常见转义字符映射
        escape_map = {
            r'\n': '\n',
            r'\t': '\t',
            r'\r': '\r',
            r'\\': '\\',
            r'\"': '"',
            r'\'': "'",
        }
        return escape_map.get(escape_seq, escape_seq)  # 如果不是特殊字符，原样返回

    # 正则匹配 \ + 特定字符（如 \n, \t 等）
    return re.sub(r'\\([nrt\\"\'])', replace_match, raw_str)


